const formattingDate = (dateString: string): string => {
  // necesito devolver "hace 3 meses", "hace 1 año", etc.
  const date = new Date(dateString);
  const now = new Date();
  const diffInMs = now.getTime() - date.getTime();
  const diffInDays = Math.floor(diffInMs / (1000 * 60 * 60 * 24));
  const diffInMonths = Math.floor(diffInDays / 30);
  const diffInYears = Math.floor(diffInDays / 365);
  if (diffInYears > 0) {
    return `hace ${diffInYears} año${diffInYears > 1 ? "s" : ""}`;
  }
  if (diffInMonths > 0) {
    return `hace ${diffInMonths} mes${diffInMonths > 1 ? "es" : ""}`;
  }
  if (diffInDays > 0) {
    return `hace ${diffInDays} día${diffInDays > 1 ? "s" : ""}`;
  }
  return "hoy";
};

export const ReviewsComponent = () => {
  const [reviewSelected, setReviewSelected] = useState<Review | null>(reviews[0]);
  const [reviewNameSelected, setReviewNameSelected] = useState<string | null>(reviews[0].name);
  const menuRef = React.useRef<HTMLUListElement>(null);
  const itemsRef = React.useRef<(HTMLLIElement | null)[]>([]);


  // animacion loop infinito
  useEffect(() => {
    // Inicializar el array de refs con la longitud correcta
    itemsRef.current = itemsRef.current.slice(0, reviews.length);

    const menu = menuRef.current;
    if (!menu) return;
    const items = itemsRef.current;

    // Wait for all refs to be populated
    const validItems = items.filter(
      (item) => item !== null && item !== undefined
    );
    if (validItems.length === 0 || validItems.length !== reviews.length) {
      // Retry after a short delay to allow refs to populate
      const timeoutId = setTimeout(() => {
        // Force re-run
      }, 10);
      return () => clearTimeout(timeoutId);
    }

    // Configuración del scroll infinito
    const containerHeight = menu.clientHeight; // 200px del contenedor
    let itemHeight = 56; // Altura fija para cada elemento (ajustar según necesites)
    let totalMenuHeight = itemHeight * validItems.length;

    // inicializamos variables de scroll en Y pues nuestro scroll infinito es vertical
    let currentScrollPosition = 0;
    let smoothScrollY = 0;
    let animationId: number;

    const lerp = (start: number, end: number, factor: number) => {
      return start * (1 - factor) + end * factor;
    };

    const adjustMenuItemsPos = (scroll: number) => {
      const currentValidItems = items.filter(
        (item) => item !== null && item !== undefined
      );
      if (currentValidItems.length > 0) {
        gsap.set(currentValidItems, {
          y: (i) => {
            const baseY = i * itemHeight + scroll;
            // Improved wrap around logic for seamless infinite scroll
            const wrappedY = gsap.utils.wrap(
              -itemHeight * 0.5, // Start a bit earlier
              totalMenuHeight - itemHeight * 0.5, // End a bit later
              baseY
            );
            return wrappedY;
          },
          opacity: (i) => {
            // Calculate opacity based on position in viewport
            const baseY = i * itemHeight + scroll;
            const wrappedY = gsap.utils.wrap(
              -itemHeight * 0.5,
              totalMenuHeight - itemHeight * 0.5,
              baseY
            );

            // Show elements only when they're within the visible area with smoother transitions
            const isVisible =  
              wrappedY >= -itemHeight &&
              wrappedY <= containerHeight + itemHeight;
            return isVisible ? 1 : 0;
          },
        });
      }
    };
    adjustMenuItemsPos(0);

    const onWheelScroll = (e: WheelEvent) => {
      e.preventDefault(); // Prevent default scroll behavior
      e.stopPropagation(); // Stop event bubbling
      currentScrollPosition += -e.deltaY;
    };

    let startY = 0;
    let currentY = 0;
    let isDragging = false;
    let speedDrag = 1.2; // Adjust drag sensitivity (1.0 = normal, >1.0 = faster, <1.0 = slower)

    const onDragStart = (e: MouseEvent | TouchEvent) => {
      e.preventDefault(); // Prevent text selection and default behavior
      isDragging = true;
      if (e instanceof TouchEvent) {
        startY = -e.touches[0].clientY;
      } else {
        startY = -e.clientY;
      }
      menu.classList.add("is-dragging");
      // Prevent text selection during drag
      document.body.style.userSelect = "none";
      menu.style.cursor = "grabbing";
    };

    const onDragMove = (e: MouseEvent | TouchEvent) => {
      if (!isDragging) return;
      e.preventDefault(); // Prevent default behavior

      if (e instanceof TouchEvent) {
        currentY = -e.touches[0].clientY;
      } else {
        currentY = -e.clientY;
      }

      // Calculate the delta (difference) and apply speed multiplier to it
      const delta = (currentY - startY) * speedDrag;
      startY = currentY; // Update startY to current position for next calculation
      currentScrollPosition += -delta; // Apply the modified delta
    };

    const onDragEnd = () => {
      isDragging = false;
      menu.classList.remove("is-dragging");
      // Restore text selection and cursor
      document.body.style.userSelect = "";
      menu.style.cursor = "grab";
    };

    const animate = () => {
      animationId = requestAnimationFrame(animate);
      smoothScrollY = lerp(smoothScrollY, currentScrollPosition, 0.1);

      adjustMenuItemsPos(smoothScrollY);

      // Optional: Add skew effect based on scroll speed if needed
      const scrollSpeed = smoothScrollY - currentScrollPosition;
      const currentValidItems = items.filter(
        (item) => item !== null && item !== undefined
      );
      if (currentValidItems.length > 0) {
        gsap.to(currentValidItems, {
          duration: 0.6,
          skewY: -scrollSpeed * 0.03,
          ease: "power3.out",
        });
      }
    };

    menu.addEventListener("wheel", onWheelScroll, { passive: false });
    menu.addEventListener("mousedown", onDragStart, { passive: false });
    menu.addEventListener("touchstart", onDragStart, { passive: false });
    window.addEventListener("mousemove", onDragMove, { passive: false });
    window.addEventListener("touchmove", onDragMove, { passive: false });
    window.addEventListener("mouseup", onDragEnd);
    window.addEventListener("touchend", onDragEnd);

    // Start the animation loop
    animate();

    // Cleanup function
    return () => {
      if (animationId) {
        cancelAnimationFrame(animationId);
      }
      menu.removeEventListener("wheel", onWheelScroll);
      menu.removeEventListener("mousedown", onDragStart);
      menu.removeEventListener("touchstart", onDragStart);
      window.removeEventListener("mousemove", onDragMove);
      window.removeEventListener("touchmove", onDragMove);
      window.removeEventListener("mouseup", onDragEnd);
      window.removeEventListener("touchend", onDragEnd);
    };
  }, [reviews.length]);


  // Animaciones de GSAP para la sección de reseñas
  useGSAP(() => {
    // Animación de entrada para la sección de reseñas
    const split = new SplitText(".review-name", { type: "words" });
    const split2 = new SplitText(".review-content", { type: "words" });
    const split3 = new SplitText(".review-date", { type: "words" });

    const tl = gsap.timeline({ delay: 0.3 });
    tl.from(
      split.words,
      {
        y: 100,
        opacity: 0,
        duration: 0.5,
      },
      0
    );
    tl.from(
      split2.words,
      {
        y: 100,
        opacity: 0,
        duration: 0.4,
      },
      0.5
    );
    tl.from(
      split3.words,
      {
        y: 100,
        opacity: 0,
        duration: 0.4,
      },
      0.5
    );
    tl.fromTo(
      ".review-logo",
      {
        opacity: 0,
        x: -50,
      },
      {
        opacity: 1,
        x: 0,
        duration: 0.5,
      },
      0.5
    );
  }, [reviewSelected]);

  return (
    <section className="w-full h-[100vh] flex flex-col items-center justify-start gap-2 bg-gray-200/80">
      {/* Infinite scroll container */}
      <h2 className="text-2xl lg:text-3xl font-roboto font-light  text-center mt-12 px-4 lg:px-0 max-w-4xl">
        Tenemos decenas de comentarios de nuestros visitantes y voluntarios. Ellos dicen de nosotros:
      </h2>
      <div className="w-full h-full flex-1 flex  flex-col-reverse lg:flex-row items-center justify-center">
        <div className="card-infinite-scroll flex flex-col h-full items-center justify-center gap-12 py-8 lg:py-16 px-4 lg:px-8 w-6/12">
          {/* <h4 className="text-2xl mx-auto bg-amber-300 font-semibold font-body">¿Qué es lo que dicen nuestros visitantes?</h4> */}
          <div className="relative w-full shadow-lg bg-white h-[320px] rounded-2xl py-8 overflow-hidden">
            <div className="absolute right-[10%] top-1/2 -translate-y-1/2 flex flex-col items-center justify-center gap-2 z-10">
              {/* flechita arriba */}
             <svg width="22" height="10" viewBox="0 0 22 10" fill="none">
            <path d="M1 9L11 1L21 9" stroke="black"/>
            </svg>
              {/* flechita abajo */}
              <svg width="22" height="10" viewBox="0 0 22 10" fill="none"  className="rotate-180">
              <path d="M1 9L11 1L21 9" stroke="black"/>
              </svg>

            </div>
            <ul
              ref={menuRef}
              className="cursor-grab relative w-full h-full select-none"
            >
              {reviews.map((rev, index) => (
                <li
                  ref={(el) => {
                    if (el) {
                      itemsRef.current[index] = el;
                    }
                  }}
                  key={index}
                  className="absolute w-full flex items-center justify-center"
                >
                  <p
                    onClick={() => {
                      setReviewSelected(rev)
                      setReviewNameSelected(rev.name)
                    }}
                    className={`font-roboto font-light text-2xl cursor-pointer text-center px-4 py-1 ${reviewNameSelected === rev.name ? 'text-white bg-black w-1/2' : ''} select-none pointer-events-auto`}
                  >
                    {rev.name}
                  </p>
                </li>
              ))}
            </ul>
          </div>
        </div>

        {/* card section */}
        <div className="card-full-content w-full h-full  flex items-center justify-center">
          <div className="font-roboto font-light card w-[80%] p-6 px-8 mx-auto min-h-[400px] rounded-2xl border shadow-xl  bg-white/90 backdrop-blur-md ">
            {reviewSelected ? (
              <div className="p-4 w-full h-full flex flex-col gap-6">
                <div className="w-full flex  items-center justify-between">
                  <div className="flex flex-col gap-2 items-start">
                    <h3 className="review-name text-4xl font-normal mb-2">
                      {reviewSelected.name}
                    </h3>
                    <p className="review-date text-sm text-gray-500">
                      {formattingDate(reviewSelected.fecha)}
                    </p>
                  </div>
                  <div className="review-logo w-12 -translate-y-4  h-12">
                    {reviewSelected.logoImage}
                  </div>
                </div>
                <p className="review-content text-2xl">
                  {reviewSelected.content}
                </p>
              </div>
            ) : (
              <p className="p-4">
                Selecciona una reseña para ver los detalles.
              </p>
            )}
          </div>
        </div>
      </div>
    </section>
  );
};
